mapper.select:
select p.id,p.job_no jobNo,p.pj_no pjNo ,p.pj_name pjName ,p.p_category pCategory ,p.name,p.sex,p.phone,p.email,p.plant_type plantType ,
p.department,p.position,p.cost_category costCategory ,p.wclass,p.line_type lineType,p.office_location officeLocation,p.director ,gl.region,
gl.pass_exam passExam,gl.certification_no certificationNo,gl.certification_date certificationDate,gl.expire_on expireOn,gl.valid,gl.blitz,gl.pja_1,gl.pja_2,gl.pja_3,gl.pja_4 from employee p left join global gl on p.job_no = gl.job_no where  p.position not like 'Operator%'


    and gl.certification_date >= #{start}


    and gl.certification_date <= #{end}


    and  gl.pass_exam= #{passExam}


    and  gl.valid = #{valid}


    and p.job_no  like  CONCAT('',#{jobNo},'%')


    and p.pj_no =  #{pjNo}


    and  p.name like CONCAT('%',#{name},'%')


    and  p.plant_type = #{plantType}


    and  p.department = #{department}


    and  p.position = #{position}


    and  p.cost_category = #{costCategory}


    and  p.director = #{director}


    and  p.line_type = #{lineType}


    and  p.entry_date >= #{entryDateStart}


    and  p.entry_date  <= #{entryDateEnd}


    and p.active = #{active}

 order by p.id asc limit #{pageOffset},#{rows}

select p.id,p.job_no jobNo,p.pj_no pjNo ,p.pj_name pjName ,p.p_category pCategory ,p.name,p.sex,p.phone,p.email,p.plant_type plantType ,
p.department,p.position,p.cost_category costCategory ,p.wclass,p.line_type lineType,p.office_location officeLocation,p.director ,gl.region,
gl.pass_exam passExam,gl.certification_no certificationNo,gl.certification_date certificationDate,gl.expire_on expireOn,gl.valid,gl.blitz,gl.pja_1,gl.pja_2,gl.pja_3,gl.pja_4 from employee p left join global gl on p.job_no = gl.job_no where  p.position not like 'Operator%'


    and gl.certification_date >= #{start}


    and gl.certification_date <= #{end}


    and  gl.pass_exam= #{passExam}


    and  gl.valid = #{valid}


    and p.job_no  like  CONCAT('',#{jobNo},'%')


    and p.pj_no =  #{pjNo}


    and  p.name like CONCAT('%',#{name},'%')


    and  p.plant_type = #{plantType}


    and  p.department = #{department}


    and  p.position = #{position}


    and  p.cost_category = #{costCategory}


    and  p.director = #{director}


    and  p.line_type = #{lineType}


    and  p.entry_date >= #{entryDateStart}


    and  p.entry_date  <= #{entryDateEnd}


    and p.active = #{active}

 order by p.id asc limit #{pageOffset},#{rows}

select count(*) from employee p left join global gl on p.job_no = gl.job_no where  p.position not like 'Operator%'

    and gl.certification_date >= #{start}


    and gl.certification_date <= #{end}


    and  gl.pass_exam= #{passExam}


    and  gl.valid = #{valid}


    and p.job_no  like  CONCAT('',#{jobNo},'%')


    and p.pj_no =  #{pjNo}


    and  p.name like CONCAT('%',#{name},'%')


    and  p.plant_type = #{plantType}


    and  p.department = #{department}


    and  p.position = #{position}


    and  p.cost_category = #{costCategory}


    and  p.director = #{director}


    and  p.line_type = #{lineType}


    and  p.entry_date >= #{entryDateStart}


    and  p.entry_date  <= #{entryDateEnd}


    and p.active = #{active}
